INSERT INTO [Posts] VALUES(N'Tuyết vẫn phủ trắng đỉnh Hoàng Liên','http://vnexpress.net/Files/Subject/3B/A2/79/21/Tuyet_250.jpg.thumb210x0.ns.jpg',N'Trưa 17/3, nhiều nơi ở huyện Sapa, Bát Xát (Lào Cai) vẫn còn đọng những lớp tuyết dày trên mái nhà và hai bên đường đi. Một số người dân dùng tuyết đắp hình người tuyết để thu tiền chụp ảnh của du khách.',N'Content','SeoUrl',N'Tag',0,1,GETDATE(),'siteadmin',NULL,NULL,1,1,GETDATE(),'siteadmin',1,@CategoryID)
